      post_content: "<!-- wp:template-part {\"slug\":\"header\",\"theme\":\"jewelry-blocks\",\"tagName\":\"header\"} /-->\n\n<!-- wp:group {\"style\":{\"spacing\":{\"margin\":{\"top\":\"0px\",\"bottom\":\"0px\"}}},\"layout\":{\"type\":\"default\"}} -->\n<div class=\"wp-block-group\" style=\"margin-top:0px;margin-bottom:0px\"><!-- wp:cover {\"url\":\"https://mlopezabogados.com.pe/wp-content/uploads/2025/02/Fondo-de-cabecera-MLOPEZ.jpg\",\"id\":96,\"dimRatio\":0,\"overlayColor\":\"base\",\"isUserOverlayColor\":false,\"minHeight\":300,\"isDark\":false,\"className\":\"inner-cover-img\",\"layout\":{\"type\":\"constrained\",\"contentSize\":\"90%\"}} -->\n<div class=\"wp-block-cover is-light inner-cover-img\" style=\"min-height:300px\"><span aria-hidden=\"true\" class=\"wp-block-cover__background has-base-background-color has-background-dim-0 has-background-dim\"></span><img class=\"wp-block-cover__image-background wp-image-96\" alt=\"\" src=\"https://mlopezabogados.com.pe/wp-content/uploads/2025/02/Fondo-de-cabecera-MLOPEZ.jpg\" data-object-fit=\"cover\"/><div class=\"wp-block-cover__inner-container\"><!-- wp:group {\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:group {\"align\":\"wide\",\"layout\":{\"type\":\"default\"}} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:post-title {\"textAlign\":\"center\",\"className\":\"wow slideInDown\",\"style\":{\"typography\":{\"textTransform\":\"uppercase\",\"letterSpacing\":\"1px\",\"fontSize\":\"42px\"}},\"textColor\":\"secondary\"} /--></div>\n<!-- /wp:group --></div>\n<!-- /wp:group --></div></div>\n<!-- /wp:cover -->\n\n<!-- wp:group {\"tagName\":\"main\",\"style\":{\"spacing\":{\"margin\":{\"top\":\"0px\",\"bottom\":\"0px\"},\"padding\":{\"top\":\"60px\",\"right\":\"20px\",\"bottom\":\"40px\",\"left\":\"20px\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<main class=\"wp-block-group\" style=\"margin-top:0px;margin-bottom:0px;padding-top:60px;padding-right:20px;padding-bottom:40px;padding-left:20px\"><!-- wp:group {\"align\":\"wide\",\"layout\":{\"type\":\"default\"}} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:post-content /--></div>\n<!-- /wp:group --></main>\n<!-- /wp:group --></div>\n<!-- /wp:group -->\n\n<!-- wp:template-part {\"slug\":\"footer\",\"theme\":\"jewelry-blocks\",\"tagName\":\"footer\",\"className\":\"custom-footer-class\"} /-->",
