  export const getPostUser = () => {
    return publicaciones.map((item)=>{
      const data = {...item};
      const dataUserAux = autores_usuarios.filter((item)=>data.post_author == item.ID);
      data["post_author_name"] = (dataUserAux.length > 0)? dataUserAux[0].display_name : "";
      data["guid"] = data.guid.replace("mlopezabogados.com.pe","concades.org.pe");
      return data;
    }).filter((item)=>{return (item.post_content != "")&&(item.post_title != "")&&(item.post_author_name != "mlopezweb1")&&(item.post_author_name != "")})
  }

  export const getPostUserId = (id) => {
    const data = getPostUser().filter((item) => {
      return item.ID == id;
    });
    return (data.length > 0)? data[0] : null;
  }